class Solution:
    def climbStairs(self, n: int) -&gt; int:
        prev = 2
        pprev = 1
        if n == 1:
            return 1
        if n == 2:
            return 2
        for _ in range(2, n):
            now = prev + pprev
            pprev = prev
            prev = now
        return now


class Solution {
public:
    int climbStairs(int n) {
        int prev = 2, pprev = 1;
        if (n == 1) return 1;
        if (n == 2) return 2;
        int now;
        for (int i = 2; i &lt; n; i++) {
            now = prev + pprev;
            pprev = prev;
            prev = now;
        }
        return now;
    }
};





