#include &lt;iostream&gt;
#include &lt;vector&gt;
#include &lt;limits&gt;

using namespace std;

void merge(vector&lt;int&gt;& A, int p, int q, int r){
    int n_1 = q-p, n_2 = r+1-q;
    vector&lt;int&gt; L(n_1), R(n_2);
    for (int i = 0; i&lt;n_1; i++){
        L[i] = A[p+i];
    }
    for (int i = 0; i&lt;n_2; i++){
        R[i] = A[q+i];
    }
    // æ·»åŠ å“¨å…µå€¼
    L.push_back(numeric_limits&lt;int&gt;::max());
    R.push_back(numeric_limits&lt;int&gt;::max());
    int i = 0, j = 0;
    for (int k = p; k &lt; r+1; k++){
        if(L[i]&lt;=R[j]){
            A[k] = L[i];
            i++;
        }
        else{
            A[k] = R[j];
            j++;
        }
    }
}

