void MergeSort(vector&lt;int&gt;& A, int p, int r){
    if (p&lt;r) {
        int q = static_cast&lt;int&gt;((p+r)/2);
        MergeSort(A, p, q);
        MergeSort(A, q+1, r);
        merge(A, p, q+1, r);
    }
}

