\begin{algorithm} \caption{threeSum(nums)} \begin{algorithmic} \State List[List[int]] result \If{nums.length&lt;3} \State \Return result\Comment{å½“æ•°ç»„é•¿åº¦ä¸è¶³3æ—¶ä¸å¯èƒ½æœ‰æ»¡è¶³æƒ…å†µçš„ç»„åˆ} \EndIf \State sort(nums)\Comment{å¯¹numsè¿›è¡Œæ’åº} \State target=0\Comment{å¯è¿›è¡Œæ‰©å±•} \For{i=0\To nums.length-2}\Comment{ä¿è¯æœ‰i,j,kçš„ç©ºé—´} \State j = i + 1 \State k = nums.length - 1 \If{i&gt;0 \And nums[i]==nums[i-1]} \Continue \EndIf \While{j&lt;k}\Comment{éå†iæŸå€¼ä¸‹çš„æ‰€æœ‰æƒ…å†µ} \If{nums[i]+nums[j]+nums[k]&lt;target} \State j = j + 1 \While{nums[j]==nums[j-1]\And j&lt;k} \State j = j + 1 \EndWhile \Elif{nums[i]+nums[j]+nums[k]&lt;target} \State k = k -1 \While{nums[k]==nums[k+1]\And j&lt;k} \State k = k - 1 \EndWhile \Else \State nums.append([nums[i],nums[j],nums[k]]) \State j = j + 1 \State k = k - 1 \While{nums[j]==nums[j-1]\And nums[k]==nums[k+1]\And j&lt;k} \State j = j + 1\Comment{j,kä¸ä¸€èµ·è¹¦çš„åŸå› ï¼Œå½“æ›´æ–°åè½å…¥è¿™ä¸ªwhileå¾ªç¯å‡ï¼Œè®¾j,kä¹‹é—´ä»…æœ‰ä¸€ä¸ªå…ƒç´ ï¼Œä¸”è¿™ä¸ªå…ƒç´ å¯¹äºjè€Œè¨€æ˜¯æ–°å€¼ï¼Œä¸€æ—¦äºŒè€…åŒæ—¶å˜åŠ¨j,kå°†ä¼šåŒæ—¶æŒ‡å‘ç›¸åŒçš„å…ƒç´ è€Œè·³å‡ºå¾ªç¯ï¼Œé€ æˆç¼ºå°‘å«æœ‰è¿™ä¸ªçš„ç»„åˆ(1,11,15)[(10,11,15)j=10,k=15,i=1]} \EndWhile \EndIf \EndWhile \EndFor \Return result \end{algorithmic} \end{algorithm}




class Solution:
    def threeSum(self, nums: List[int]) -&gt; List[List[int]]:
        result = []
        if len(nums) &lt; 3:
            return result
        target = 0
        nums.sort()
        for i in range(len(nums)-2):
            if i &gt; 0 and nums[i] == nums[i-1]: continue
            j = i + 1
            k = len(nums) - 1
            while j &lt; k:
                if (nums[i] +  nums[j] + nums[k] &lt; target):
                    j += 1
                    while nums[j] == nums[j-1] and j &lt; k: j += 1
                elif (nums[i] + nums[j] + nums[k] &gt; target):
                    k -= 1
                    while nums[k] == nums[k+1] and j &lt; k: k -= 1
                else:
                    result.append([nums[i], nums[j], nums[k]])
                    j += 1
                    k -= 1
                    while (nums[j] == nums[j-1] and nums[k] == nums[k+1] and j &lt; k):
                        j += 1
        return result


class Solution {
public:
    vector&lt;vector&lt;int&gt;&gt; threeSum(vector&lt;int&gt;& nums) {
        vector&lt;vector&lt;int&gt;&gt; result;
        if (nums.size() &lt; 3) return result;
        sort(nums.begin(), nums.end());
        auto last = nums.end();
        const int target = 0;
        for (auto i = nums.begin(); i &lt; last - 2; i++){
            auto j = i + 1;
            if (i &gt; nums.begin() && *i == *(i-1)) continue;
            auto k = last - 1;
            while (j &lt; k){
                if (*i + *j + *k &lt; target){
                    j++;
                    while (*j == *(j-1) && j &lt; k) j++;
                }
                else if (*i + *j + *k &gt; target){
                    k--;
                    while (*k == *(k+1) && j &lt; k) k--;
                }
                else {
                    result.push_back({*i, *j, *k});
                    j++;
                    k--;
                    while (*j == *(j-1) && *k==*(k+1) && j &lt; k) j++;
                }
            }
        }
        return result;
    }
};





