/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode() : val(0), next(nullptr) {}
 *     ListNode(int x) : val(x), next(nullptr) {}
 *     ListNode(int x, ListNode *next) : val(x), next(next) {}
 * };
 */
class Solution {
public:
    ListNode* addTwoNumbers(ListNode* l1, ListNode* l2) {
1        ListNode *head = nullptr, *tail = nullptr;
        int carry = 0;
        while (l1 || l2) {
            int n1 = l1 ? l1-&gt;val: 0;
            int n2 = l2 ? l2-&gt;val: 0;
            int sum = n1 + n2 + carry;
            if (!head) {
2                head = tail = new ListNode(sum % 10);
            } else {
3                tail-&gt;next = new ListNode(sum % 10);
                tail = tail-&gt;next;
            }
            carry = sum / 10;
            if (l1) {
                l1 = l1-&gt;next;
            }
            if (l2) {
                l2 = l2-&gt;next;
            }
        }
        if (carry &gt; 0) {
            tail-&gt;next = new ListNode(carry);
        }
        return head;
    }
};

