\begin{algorithm} \caption{longestConsecutive(nums)} \begin{algorithmic} \State å“ˆå¸Œè¡¨ used \For{i in nums} \State used[i] = false \Comment{åˆå§‹åŒ–æ‰€æœ‰ç´¢å¼•è®¤ä¸ºå‡æœªéå†} \EndFor \State longest = 0\Comment{è®¾ç½®åˆå§‹åŒ–æœ€å¤§è¿ç»­é•¿åº¦} \For{i in nums}\Comment{å¼€å§‹éå†å…¨éƒ¨å…ƒç´ è¿™ä¹Ÿæ˜¯æ—¶é—´å¤æ‚åº¦ä¸º$O(n)$çš„åŸå› } \If{used[i]} \Continue\Comment{è‹¥è¢«éå†è¿‡è¯´æ˜å…¶å·²æ‰¾åˆ°å±äºå®ƒçš„æœ€å¤§è¿ç»­å­æ•°ç»„ï¼Œæ²¡æœ‰å¿…è¦å†éå†} \EndIf \State length = 1\Comment{åˆå§‹åŒ–æœ€å¤§è¿ç»­é•¿åº¦} \State used[i] = True\Comment{æ ‡è®°è¯¥å…ƒç´ å·²ç»è¢«éå†} \State j = i + 1\Comment{å¼€å§‹éå†å…¶å³ä¾§çš„å€¼ç›´è‡³ä¸å†è¿ç»­} \While{j in used.keys} \State used[j] = true \State length += 1\Comment{æ‰¾åˆ°äº†ä¸€ä¸ªè¿ç»­çš„} \State j += 1 \EndWhile \State j = i - 1\Comment{ç»§ç»­å¯»æ‰¾iå·¦ä¾§çš„è¿ç»­å€¼} \While{j in used.keys} \State used[j] = true \State length += 1 \State j -= 1 \EndWhile \State longest = max(length, longest) \EndFor \Return longest \end{algorithmic} \end{algorithm}




class Solution {
public:
    int longestConsecutive(vector&lt;int&gt;& nums) {
        unordered_map&lt;int, bool&gt; used;  // åˆ›å»ºå“ˆå¸Œè¡¨
        for (auto i : nums) used[i] = false;
        int longest = 0;
        for (auto i : nums){  // åˆ©ç”¨auto i : numséå†numsä¸­çš„å€¼
            if (used[i]) continue;
            int length = 1;
            used[i] = true;
            for (int j = i + 1; used.find(j) != used.end(); ++j){
                used[j] = true;
                ++length;
            }
            for (int j = i - 1;used.find(j) != used.end(); --j){
                used[j] = true;
                ++length;
            }
            longest = max(longest, length);
        }
        return longest;
    }
};


class Solution:
    def longestConsecutive(self, nums: List[int]) -&gt; int:
        used = dict();
        for i in nums:
            used[i] = False
        longest = 0
        for i in nums:
            if used[i]: continue
            length = 1
            used[i] = True
            j = i + 1
            while j in used:
                used[j] = True
                length += 1
                j += 1
            j = i - 1
            while j in used:
                used[j] = True
                length += 1
                j -= 1
            longest = max(length, longest)
        return longest






