class Solution:
    def fourSum(self, nums: List[int], target: int) -&gt; List[List[int]]:
        nums.sort()  # å¯¹æ•°ç»„è¿›è¡Œæ’åºO(log(n))
        result = []
        for i in range(len(nums) - 3):
            if i &gt; 0 and nums[i] == nums[i-1]: continue  # ä¿è¯ä¸å†éå†ä¹‹å‰çš„å€¼
            for j in range(i + 1, len(nums) - 2):
                if j &gt; i + 1 and nums[j] == nums[j-1]: continue  # ä¿è¯ä¸åœ¨éå†ä¹‹å‰çš„å€¼
                k = j + 1
                m = len(nums) - 1
                while k &lt; m:
                    if nums[i] + nums[j] + nums[k] + nums[m] &lt; target:
                        k += 1
                        while nums[k] == nums[k-1] and k &lt; m: k += 1
                    elif nums[i] + nums[j] + nums[k] + nums[m] &gt; target:
                        m -= 1
                        while nums[m] == nums[m+1] and k &lt; m: m -= 1
                    else:
                        result.append([nums[i], nums[j], nums[k], nums[m]])
                        k += 1
                        m -= 1
                        while nums[k] == nums[k-1] and nums[m] == nums[m+1] and k &lt; m: k += 1
        return result


class Solution {
public:
    vector&lt;vector&lt;int&gt;&gt; fourSum(vector&lt;int&gt;& nums, int target) {
        vector&lt;vector&lt;int&gt;&gt; result;
        sort(nums.begin(), nums.end());
        auto last = nums.end();
        for (auto i = nums.begin(); i &lt; last - 3; i++){
            if (i &gt; nums.begin() && *i == *(i-1)) continue;
            if ((long) *i + *(i + 1) + *(i + 2) + *(i + 3) &gt; target) break;  // æœ€å°çš„ä¸‰ä¸ªæ•°éƒ½æ²¡targetå¤§çš„è¯è¿™ä¸ªæ•°ç»„å°±ä¸ä¼šæœ‰äº†
            if ((long) *i + *(last - 1) + *(last - 2) + *(last - 3) &lt; target) continue;  // ç›®å‰è·Œè¿­ä»£å€¼å’Œæœ€å¤§çš„ä¸‰ä¸ªæ•°éƒ½æ²¡æœ‰targetå¤§çš„è¯æ­¤æ¬¡è¿­ä»£å°±ä¸ä¼šæœ‰äº†
            for (auto j = i + 1; j &lt; last - 2; j++){
                if (j &gt; i + 1 && *j == *(j-1)) continue;
                if ((long) *i + *j + *(j + 1) + *(j + 2) &gt; target) break;
                if ((long) *i + *j + *(last - 1) + *(last - 2) &lt; target) continue;
                auto k = j + 1;
                auto m = nums.end() - 1;
                while (k &lt; m){
                    if ((long) *i + *j + *k + *m &lt; target){
                        k++;
                        while (*k == *(k - 1) && k &lt; m) k++;
                    }
                    else if ((long) *i + *j + *k + *m &gt; target){
                        m--;
                        while (*m == *(m + 1) && k &lt; m) m--;
                    }
                    else{
                        result.push_back({*i, *j, *k, *m});
                        k++;
                        m--;
                        while (*k == *(k - 1) && *m == *(m + 1) && k &lt; m) k++;
                    }
                }
            }
        }
        return result;
    }
};





