class Solution:
    def nextPermutation(self, nums: List[int]) -&gt; None:
        """
        Do not return anything, modify nums in-place instead.
        """
        a = []; b=[]
        for i in range(len(nums)-2, -1, -1):
            # æ‰¾a
            if nums[i] &lt; nums[i + 1]:
                a = a + [i, nums[i]]
                break
        if a:      
            for i in range(len(nums)-1, a[0], -1):
                # æ‰¾b
                if nums[i] &gt; a[1]:
                    b = b + [i, nums[i]]
                    break
            nums[a[0]] = b[1]
            nums[b[0]] = a[1]
            nums[a[0]+1:] = sorted(nums[a[0] + 1:])
        else:
            nums.sort()


class Solution {
public:
