        nextPermutation(nums.begin(), nums.end());
   }

    // æ¨¡æ¿å‡½æ•°BidItç”¨äºå ä½å¦‚vectorï¼Œlistç­‰å¯åŒå‘è·Œæ‰“çš„
    template&lt;typename BidiIt&gt;
    bool nextPermutation(BidiIt first, BidiIt last) {
        const auto rfirst = reverse_iterator&lt;BidiIt&gt;(last);  // åˆ›å»ºåå‘è¿­ä»£å™¨
        const auto rlast = reverse_iterator&lt;BidiIt&gt;(first);

        auto pivot = next(rfirst);
        while (pivot != rlast && *pivot &gt;= *prev(pivot)) ++pivot;
        if (pivot == rlast) {
            reverse(rfirst, rlast);
            return false;
        }  // å½“æ•°ç»„æœ¬èº«ä»¥å…¨éƒ¨é€†åºï¼Œåˆ™ä¸‹ä¸€ä¸ªæ’åˆ—åªéœ€é€†åºå³å¯

        auto change = find_if(rfirst, pivot, [&pivot](const auto& elem) {
            return elem &gt; *pivot;});  // lambdaå‡½æ•°,find_ifå‡½æ•°ç”¨äºæŒ‡å®šæœç´¢èŒƒå›´å’Œæœç´¢æ¡ä»¶
        swap(*change, *pivot);
        reverse(rfirst, pivot);
        return true;
    }

};







