class Solution:
    def plusOne(self, digits: List[int]) -&gt; List[int]:
        plus = 1
        for i in range(len(digits)-1, -1, -1):
            if digits[i] + plus &gt;= 10:
                digits[i] = 0
                plus = 1
            else:
                digits[i] += plus
                break
            if i == 0 and plus == 1:
                digits.insert(0, plus)
        return digits


class Solution {
public:
    vector&lt;int&gt; plusOne(vector&lt;int&gt;& digits) {
        auto last = digits.end();
        for (auto i = last-1; i &gt;= digits.begin(); i--) {
            if ((*i + 1) == 10) {
                *i = 0;
            } else {
                *i = *i + 1;
                break;
            }
            if (i == digits.begin()) digits.insert(digits.begin(), 1);
        }
        return digits;
    }
};







