class Solution {
public:
    ListNode* removeNthFromEnd(ListNode* head, int n) {
        ListNode dummy{-1, head};
        ListNode *fast = &dummy, *slow = &dummy;
        for (int i = 0; i &lt; n; i++) {
            fast = fast-&gt;next;
        }
        while (fast-&gt;next) {
            fast = fast-&gt;next;
            slow = slow-&gt;next;
        }
        ListNode *temp = slow-&gt;next;
        slow-&gt;next = slow-&gt;next-&gt;next;
        delete temp;
        return dummy.next;
    }
};


