        ListNode dummy{-1, head};
        for (ListNode *pre = &dummy, *cur = pre-&gt;next, *next = cur-&gt;next; next; pre = cur, cur = cur-&gt;next, next = cur ? cur-&gt;next : nullptr) {
            pre-&gt;next = next;
            cur-&gt;next = next-&gt;next;
            next-&gt;next = cur;
        }
        return dummy.next;
    }
};


