class Solution:
    def reverseKGroup(self, head: Optional[ListNode], k: int) -&gt; Optional[ListNode]:
        if head is None or head.next is None or k &lt; 2: return head
        dummy = ListNode(next = head)
        pre = dummy; end = head
        while end:
            for _ in range(k-1):
                end = end.next
                if end is None: return dummy.next
            cur = pre
            pre.next, tail = self.reverse(cur, k)
            end = tail.next
            pre = tail     
        return dummy.next

    def reverse(self,cur: Optional[ListNode], k: int) -&gt; tuple[Optional[ListNode], Optional[ListNode]]:
        head2 = cur
        cur = cur.next; next = cur.next
        for _ in range(k - 1):
            cur.next = next.next
            next.next = head2.next
            head2.next = next
            next = cur.next
        return head2.next, cur


/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode() : val(0), next(nullptr) {}
 *     ListNode(int x) : val(x), next(nullptr) {}
 *     ListNode(int x, ListNode *next) : val(x), next(next) {}
 * };
 */
class Solution {
public:
    ListNode* reverseKGroup(ListNode* head, int k) {
        if (!head || !head-&gt;next || k &lt; 2) return head;
        ListNode dummy{-1, head};
        ListNode *end = head, *pre = &dummy;
        while (end) {
            // æŸ¥çœ‹æ˜¯å¦æœ‰kä¸ªèŠ‚ç‚¹ç”¨äºç¿»è½¬
            for (int i =  0; i &lt; k - 1 ; i++) {
                end = end-&gt;next;
                if (!end) return dummy.next;
            }
            tie(pre-&gt;next, end, pre) = reverse(pre, k);
        }
        return dummy.next;
    }

private:
    tuple&lt;ListNode*, ListNode*, ListNode*&gt; reverse(ListNode *cur, int k) {
