class Solution {
public:
    bool isMatch(string s, string p) {
        class InnerClass {
        public:
            string s;
            string p;
            InnerClass(string &s, string &p) {
                this-&gt;s = s;
                this-&gt;p = p;
            }
            bool match(int i, int j) {
                if (i == 0) return false;
                return this-&gt;p[j-1] == '.' ? true : this-&gt;s[i-1] == this-&gt;p[j - 1];
            }
        };  // å†…éƒ¨ç±»çš„å£°æ˜-ä¸ºäº†å®ç°å†…éƒ¨å‡½æ•°
        InnerClass Inner(s, p);
        int n = s.size(), m = p.size();
        std::vector&lt;std::vector&lt;bool&gt;&gt; dp(n + 1, std::vector&lt;bool&gt;(m + 1, false));  // åˆ›å»ºboolç±»å‹çš„äºŒç»´è¡¨
        dp[0][0] = true;  // è¾¹ç•Œæ¡ä»¶
        for (int i = 0; i &lt; n + 1; i++) {
            for (int j = 1; j &lt; m + 1; j++) {
                if (p[j-1] != '*') {
                    dp[i][j] = Inner.match(i, j) ? dp[i-1][j-1] : false;
                }
                else {
                    dp[i][j] = Inner.match(i, j-1) ? (dp[i-1][j] || dp[i][j-2]) : dp[i][j-2];
                }
            }
        }
        return dp[n][m];

    }
};


