        std::vector&lt;std::vector&lt;bool&gt;&gt; dp(n+1, std::vector&lt;bool&gt;(m+1, false));
        dp[0][0] = true;  // åˆå§‹åŒ–ä¸è¦å¿˜äº†ï¼ï¼
